package br.com.elvisassis.mscartoes.application;import br.com.elvisassis.mscartoes.dto.CardClientDTO;import br.com.elvisassis.mscartoes.infra.repository.CardClientRepository;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.lang.module.ResolutionException;import java.util.List;@Service@RequiredArgsConstructorpublic class CardClientService {    private final CardClientRepository repositor;    public List<CardClientDTO> findByCpf(String cpf) {        var cardsClients =  repositor.findByCpf(cpf)                .orElseThrow(() -> new ResolutionException("Não cartão disponivel!"));        return cardsClients.stream()                .map(CardClientDTO::new)                .toList();    }}