package br.com.elvisassis.msclientes.dto;import br.com.elvisassis.msclientes.domain.Client;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class ClientDTO {    private Long id;    private String cpf;    private String name;    private String age;    public ClientDTO(Client client) {        this.id = client.getId();        this.cpf = client.getCpf();        this.name = client.getName();        this.age = client.getAge();    }    public Client toModel(){        return new Client(id, cpf, name, age);    }    public ClientDTO toDTO(Client client) {        return new ClientDTO(client);    }}