package br.com.elvisassis.msavaliadorcredito.application;import br.com.elvisassis.msavaliadorcredito.application.ex.ClientDataNotFoundException;import br.com.elvisassis.msavaliadorcredito.application.ex.ErrorComunicationMicroServicesException;import br.com.elvisassis.msavaliadorcredito.domain.model.ClientSituation;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/avaliacoes-credito")@RequiredArgsConstructorpublic class CreditAssessorController {    private final CreditAssessorService creditAssessorservice;    @GetMapping    public String status() {        return "ok";    }    @GetMapping(value = "situacao-cliente", params = "cpf")    public ResponseEntity checkClientStatus(@RequestParam("cpf") String cpf) {        try {            ClientSituation clientSituation = creditAssessorservice.getSituationClinte(cpf);        return ResponseEntity.ok(clientSituation);        } catch (ClientDataNotFoundException e) {            return ResponseEntity.notFound().build();        } catch (ErrorComunicationMicroServicesException e) {            return ResponseEntity.status(HttpStatus.resolve(e.getStatus()))                    .body(e.getMessage());        }    }}