package br.com.elvisassis.msavaliadorcredito.infra.mqueue;import br.com.elvisassis.msavaliadorcredito.domain.model.CardIssuanceRequestData;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.ObjectMapper;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.amqp.core.Queue;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.stereotype.Component;@Slf4j@Component@RequiredArgsConstructorpublic class CardIssueRequestPublisher {    private final RabbitTemplate rabbitTemplate;    private final Queue cardIssuequeue;    public void cardIssue(CardIssuanceRequestData data) throws JsonProcessingException {        var json = convertIntoJson(data);        log.info("--------- Iniciando a solicitação de cartão para o cpf: " + data.getCpf() + " ---------");        rabbitTemplate.convertAndSend(cardIssuequeue.getName(), json);        log.info("-------- Solicitação concluida com sucessso ---------");    }    private String convertIntoJson(CardIssuanceRequestData data) throws JsonProcessingException {        ObjectMapper mapper = new ObjectMapper();        var json = mapper.writeValueAsString(data);        return json;    }}