package br.com.elvisassis.mscartoes.application;import br.com.elvisassis.mscartoes.dto.CardClientDTO;import br.com.elvisassis.mscartoes.dto.CardDTO;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/cartoes")@RequiredArgsConstructorpublic class CardController {    private final CardService service;    private final CardClientService cardClientService;    @GetMapping    public String status() {        return "ok";    }    @PostMapping    public ResponseEntity<CardDTO> save(@RequestBody CardDTO cardDTO) {        var response = service.save(cardDTO);        return ResponseEntity                .status(HttpStatus.CREATED).body(response);    }    @GetMapping(value = "/{income}")    public ResponseEntity<List<CardDTO>> findByIncomeLessThenEqual(            @PathVariable(name = "income") Long income) {            var response = service.findCardIcomeLasThenEqual(income);            return ResponseEntity.ok(response);    }    @GetMapping(params = "cpf")    public ResponseEntity<List<CardClientDTO>> findByCpf(@RequestParam(name = "cpf") String cpf) {        var response = cardClientService.findByCpf(cpf);        return ResponseEntity.ok(response);    }}