package br.com.elvisassis.mscartoes.domain;import jakarta.persistence.*;import lombok.Data;import lombok.NoArgsConstructor;import java.math.BigDecimal;@Entity@Datapublic class Card {    public Card() {}    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "name")    private String name;    @Column(name = "flag")    @Enumerated(EnumType.STRING)    private FlagCard flag;    @Column(name = "income")    private BigDecimal income;    @Column(name = "basic_limit")    private BigDecimal basicLimit;    public Card(String name, FlagCard flag, BigDecimal income, BigDecimal basicLimit) {        this.name = name;        this.flag = flag;        this.income = income;        this.basicLimit = basicLimit;    }    public Card(CardClient cardClient) {}}