package br.com.elvisassis.mscartoes.dto;import br.com.elvisassis.mscartoes.domain.CardClient;import lombok.Data;import lombok.NoArgsConstructor;import java.math.BigDecimal;@Data@NoArgsConstructorpublic class CardClientDTO {    private Long id;    private String name;    private String flag;    private BigDecimal limitAvailable;    public CardClientDTO(CardClient entity) {        this.id = entity.getId();        this.name = entity.getCard().getName();        this.flag = entity.getCard().getFlag().toString();        this.limitAvailable = entity.getLimit();    }}