package br.com.elvisassis.mscartoes.dto;import br.com.elvisassis.mscartoes.domain.Card;import br.com.elvisassis.mscartoes.domain.FlagCard;import lombok.Data;import java.math.BigDecimal;@Datapublic class CardDTO {    private Long id;    private String name;    private FlagCard flag;    private BigDecimal income;    private BigDecimal basicLimit;    public CardDTO() {}    public Card toModel() {        return new Card(name, flag, income, basicLimit);    }    public CardDTO(Card card) {        this.id = card.getId();        this.name = card.getName();        this.flag = card.getFlag();        this.income = card.getIncome();        this.basicLimit = card.getBasicLimit();    }}