package br.com.elvisassis.msclientes.application;import br.com.elvisassis.msclientes.dto.ClientDTO;import br.com.elvisassis.msclientes.infra.repository.ClientRepository;import jakarta.transaction.Transactional;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructorpublic class ClientService {    private final ClientRepository repository;    @Transactional    public ClientDTO save(ClientDTO clientDTO) {        var client = clientDTO.toModel();        repository.save(client);        return new ClientDTO(client);    }    public ClientDTO findByCPF(String cpf) {        var client = repository.findByCpf(cpf)                .orElseThrow(                        () -> new RuntimeException("Clinte n√£o encontrado"));        return new ClientDTO(client);    }}